// css
import "../css/signup.css";
import axios from "axios";

// library
import { Ref, useRef, useState } from "react";
import { Form, Link } from "react-router-dom";

// other file
import { createFormData } from "../util/form";
import { user_service } from "../service/userservice";
import { errorHandling } from "../error/error";

// types
import { User } from "../types/user";
import { authErrorProps, SignupError } from "../types/auth";
import { InputChange, FormSubmit } from "../types/event";
import { emailValidate, passwordValidate } from "../validation/auth";

class AppError extends Error {
  details: Record<string, any>;

  constructor(message: string, details: Record<string, any> = {}) {
    super(message);
    this.name = "AppError";
  }
}

export const Signup = () => {
  let [SignupStep, setSignupStep] = useState<boolean>(false);
  let [error, setError] = useState<authErrorProps>({
    state: false,
    message: "",
  });
  let emailRef = useRef<HTMLInputElement | null>(null);
  let nicknameRef = useRef<HTMLInputElement | null>(null);
  let passwordRef = useRef<HTMLInputElement | null>(null);
  let passwordCheckRef = useRef<HTMLInputElement | null>(null);

  let [email, setEmail] = useState<string>("");
  let [nickname, setNickname] = useState<string>("");
  let [password, setPassword] = useState<string>("");
  let [password_check, setPasswordCheck] = useState<string>("");

  // 인증부분
  let [authcode, setAuthcode] = useState<string>("");
  let AuthInputRef = useRef<HTMLInputElement | null>(null);

  // 에러처리
  const errorHandler = (state: boolean, message?: string) => {
    setError((c) => ({ ...c, state, message }));
  };

  // 유저 정보 보내기
  const submitUserInfo = async (e: FormSubmit) => {
    e.preventDefault();

    // 유저 정보값 검증 (클라이언트)
    if (!emailValidate(email)) {
      throw new Error({});
    }
    if (nickname.trim().length < 2 || nickname.trim().length > 12) {
      errorHandler(true, SignupError.NICKNAME);
      if (nicknameRef.current) nicknameRef.current.focus();
      return;
    }
    if (!passwordValidate(password)) {
      errorHandler(true, SignupError.PASSWORD);
      if (passwordRef.current) passwordRef.current.focus();
      return;
    }
    if (password !== password_check) {
      errorHandler(true, SignupError.PASSWORD_CHECK);
      if (passwordCheckRef.current) passwordCheckRef.current.focus();
      return;
    }

    // formdata
    let userInfo = createFormData<User>({
      email,
      nickname,
      password,
      password_check,
    });

    try {
      let res = await user_service.sendUserInfo(userInfo);
      console.log("success signup first step");
    } catch (err: unknown) {
      let error = errorHandling(err);
      console.log(error);
    }
  };

  return (
    <div className="page signup_page">
      {SignupStep ? (
        // 회원가입 이메일 인증
        <div className="signup_auth_box">
          {/* header section */}
          <div className="signup_auth_header">
            {email}로 인증번호가 전송되었습니다
            <button className="signup_auth_resend_btn">번호 재전송</button>
          </div>
          {/* content section */}
          <div className="signup_auth_content">
            <form method="post">
              {/* error */}
              <div
                className="error_auth_box"
                style={{ display: error.state ? "flex" : "none" }}
              >
                {error.message}
              </div>
              <input
                type="text"
                ref={AuthInputRef}
                value={authcode}
                placeholder="인증번호를 입력하세요"
                onChange={(e: InputChange) => setAuthcode(e.target.value)}
              />

              <button className="">인증하기</button>
            </form>
          </div>
          <footer className="signup_auth_footer">
            <button>돌아가기</button>
          </footer>
        </div>
      ) : (
        // 회원가입 유저정보
        <div className="signup_container">
          {/* 로그인 헤더 */}
          <div className="signup_header">
            <span>PWF</span>
          </div>
          {/* 로그인 내용 */}
          <div className="signup_content">
            <form action="post" onSubmit={submitUserInfo}>
              {/* error box */}
              <div
                className="error_box"
                style={{ display: error.state ? "flex" : "none" }}
              >
                {error.message}
              </div>
              <input
                type="email"
                spellCheck={false}
                placeholder="이메일"
                ref={emailRef}
                value={email}
                onChange={(e: InputChange) => setEmail(e.target.value)}
              />
              <input
                type="text"
                placeholder="닉네임"
                ref={nicknameRef}
                spellCheck={false}
                value={nickname}
                onChange={(e: InputChange) => setNickname(e.target.value)}
              />
              <input
                type="password"
                placeholder="비밀번호"
                ref={passwordRef}
                value={password}
                onChange={(e: InputChange) => setPassword(e.target.value)}
              />
              <input
                type="password"
                placeholder="비밀번호 확인"
                ref={passwordCheckRef}
                value={password_check}
                onChange={(e: InputChange) => setPasswordCheck(e.target.value)}
              />
              <button>회원가입</button>
            </form>
          </div>
          {/* 로그인 풋터 */}
          <div className="signup_footer">
            <span>계정이 있다면?</span>
            <Link to="/login" className="login_anchor">
              로그인
            </Link>
          </div>
        </div>
      )}
    </div>
  );
};
