import { useEffect, useState } from "react";

import { emitter } from "../../util/event";

// css
import "../../css/room/menu.css";

// component
import { Chat } from "./chat";
import { Participants } from "./participants";
import { User } from "../../types/user";
import { Room } from "../../types/room";

export const Menu = () => {
  let [menu, setMenu] = useState<boolean>(true);
  return (
    <div className="pwf-stream_menu">
      <button className="stream_menu_btn">
        <i className="fa-solid fa-chevron-left"></i>
      </button>

      <ul className="stream_menu_content">
        <button>
          <i
            className={`fa-solid fa-microphone-lines${audio ? "" : "-slash"}`}
          ></i>
        </button>
        {/* 비디오 */}
        <button>
          <i className={`fa-solid fa-video${video ? "" : "-slash"}`}></i>
          <span>{video ? "비디오" : "비디오 해제"}</span>
        </button>
        {/* 방에 친구초대 */}
        <button onClick={() => openModal("invitation")}>
          <i className="fa-solid fa-user-plus"></i>
          <span>초대</span>
        </button>
        {/* 화면공유 */}
        <button
          onClick={ShareStart}
          style={{ color: otherShare.state ? "var(--pwf-blue)" : "white" }}
        >
          <i className="fa-brands fa-creative-commons-share"></i>
          <span>화면공유</span>
        </button>
        {/* 내 미디어 변경 */}
        <button>
          <i className="fa-solid fa-desktop"></i>
          <span>내 미디어</span>
        </button>
        {/* 메뉴 */}
        <button onClick={() => emitter.emit("room menu", true)}>
          <i className="fa-brands fa-elementor"></i>
          <span>메뉴</span>
        </button>
        <button className="room_exit" onClick={leaveRoom}>
          방나가기
        </button>
      </ul>
    </div>
  );
};
