import "../css/friend.css";
import { useState, useEffect, ChangeEvent } from "react";
import { emitter } from "../util/event";
import { FormSubmit, InputChange } from "../types/event";
import { user_service } from "../service/userservice";
import { createFormData } from "../util/form";
import { errorHandling } from "../error/error";

export const FriendModal = () => {
  let [open, setOpen] = useState<boolean>(false);
  let [error, setError] = useState<{ state: boolean; msg: string }>({
    state: false,
    msg: "",
  });
  let [nickname, setNickname] = useState<string>("");

  const searchNickname = async (e: FormSubmit) => {
    e.preventDefault();

    try {
      let user = await user_service.nicknameSearch(nickname);
      console.log(user);
    } catch (err) {
      let { msg } = errorHandling(err);
      setError((c) => ({ ...c, state: true, msg }));
    }
  };

  useEffect(() => {
    const handler = (open: boolean) => setOpen(open);
    emitter.on("friend modal", handler);

    return () => {
      emitter.off("friend modal", handler);
    };
  }, []);

  return (
    <div className="modal_box" style={{ display: open ? "flex" : "none" }}>
      <div className="pwf_friend_modal">
        {/* 모달 헤더 */}
        <header className="modal_header">
          <button onClick={() => setOpen(false)}>X</button>
        </header>

        <div className="friend_content">
          {/* 친추요청 검색 */}
          <div className="friend_search_box">
            <form className="friend_search_form" onSubmit={searchNickname}>
              <input
                type="text"
                style={{
                  border: `1px solid var(--pwf-${
                    error.state ? "red" : "gray"
                  })`,
                }}
                value={nickname}
                onChange={(e: InputChange) => {
                  setNickname(e.target.value);
                  setError((c) => ({ ...c, state: false }));
                }}
                placeholder="친구요청 닉네임"
                className="friend_search_input"
              />
            </form>
            <span
              className="friend_search_error"
              style={{ display: error.state ? "flex" : "none" }}
            >
              {error.msg}
            </span>
          </div>
          {/* 친추 리스트 */}
          <div className="friend_request_box">
            <label>친구요청</label>
            <ul className="request_friends"></ul>
          </div>
        </div>
      </div>
    </div>
  );
};
