import { useEffect, useRef, useState } from "react";
import "../css/streamRoom.css";
import { useNavigate, useParams } from "react-router-dom";
import { socketClient } from "../util/socket";
import { User } from "../types/user";
import { stream_service } from "../service/streamservice";
import { Room } from "../types/room";

export const MenuParticipants = () => {
  return <div>participants</div>;
};

export const Menuchat = () => {
  return <div>chat</div>;
};

export const StreamRoom = () => {
  let { id } = useParams();
  let navigate = useNavigate();

  // ref
  let localRef = useRef<HTMLVideoElement>(null);
  let remoteRef = useRef<HTMLVideoElement>(null);
  const peer = useRef<RTCPeerConnection | null>(null);
  const otherUser = useRef<string | null>(null);

  // state
  let [room, setRoom] = useState<Room>({});
  let [menu, setMenu] = useState<{
    state: boolean;
    type: "participants" | "chat";
  }>({ state: false, type: "participants" }); //메뉴
  let [audio, setAudio] = useState<boolean>(true); //오디오
  let [video, setVideo] = useState<boolean>(true); //비디오
  let [connectList, setConnectList] = useState<
    { nickname: string; stream: MediaStream }[]
  >([]);

  // 시작시 내 스트림
  useEffect(() => {
    // 유저 미디오 가져오기
    const start = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({
          video,
          audio,
        });
        localRef.current!.srcObject = stream;

        peer.current = new RTCPeerConnection(iceServer);
        stream
          .getTracks()
          .forEach((track) => peer.current?.addTrack(track, stream));

        peer.current.ontrack = (event) => {
          remoteRef.current!.srcObject = event.streams[0];
        };

        peer.current.onicecandidate = (event) => {
          if (event.candidate && otherUser.current) {
            socketClient.emit("ice-candidate", {
              target: otherUser.current,
              candidate: event.candidate,
            });
          }
        };

        socketClient.emit("join", `room${id}`);
        socketClient.on("user-joined", (userId) => {
          otherUser.current = userId;
        });

        socketClient.on("other-user", async (userId) => {
          otherUser.current = userId;
          const offer = await peer.current?.createOffer();
          await peer.current?.setLocalDescription(offer!);
          socketClient.emit("offer", {
            target: userId,
            sdp: offer,
          });
        });

        // offer
        socketClient.on("offer", async ({ sdp, sender }) => {
          otherUser.current = sender;
          await peer.current?.setRemoteDescription(
            new RTCSessionDescription(sdp)
          );
          const answer = await peer.current?.createAnswer();
          await peer.current?.setLocalDescription(answer);
          socketClient.emit("answer", { target: sender, sdp: answer });
        });
        // answer
        socketClient.on("answer", async ({ sdp }) => {
          if (!peer.current?.currentRemoteDescription)
            await peer.current?.setRemoteDescription(
              new RTCSessionDescription(sdp)
            );
        });
        // candidate
        socketClient.on("ice-candidate", async ({ candidate }) => {
          try {
            await peer.current?.addIceCandidate(new RTCIceCandidate(candidate));
          } catch (err) {
            console.log(err);
          }
        });
      } catch (err) {
        console.log(err);
      }
    };
    start();
  }, []);

  // 시작시 방 정보 얻어옴
  useEffect(() => {
    let data = async () => {
      try {
        let room = await stream_service.getStreamRoomData(id!);
        setRoom(room);
      } catch (err) {
        console.log(err);
      }
    };
    data();
  }, []);

  // function -----------------------------

  // 화면 on/off
  // let toggleVideo = async () => {
  //   if (!streamRef.current) return;

  //   streamRef.current.getVideoTracks().forEach((track) => {
  //     track.enabled = !track.enabled;
  //   });
  //   setVideo(!video);
  // };
  // 오디오 on/offs
  // let toggleAudio = async () => {
  //   if (!streamRef.current) return;

  //   streamRef.current.getAudioTracks().forEach((track) => {
  //     track.enabled = !track.enabled;
  //   });
  //   setAudio(!audio);
  // };
  // 방을 떠남
  let leaveRoom = () => {
    navigate("/");
    socketClient.emit("leave room", `room${id}`);
  };

  return (
    <div className="page streamRoom_page">
      {/* main */}
      <div className="pwf-streamRoom_container">
        {/* 화면창 */}
        <ul className="user_screen_lists">
          <video
            ref={localRef}
            autoPlay
            playsInline
            className="user_video"
          ></video>
          {/* 상대방 화면 */}
          <video
            ref={remoteRef}
            autoPlay
            playsInline
            className="user_video"
          ></video>
        </ul>
        {/* main footer */}
        <footer className="streamRoom_footer">
          {/* 유저 음성 및 비디오 */}
          <div className="user_video_options">
            {/* 음성 */}
            {/* <button onClick={toggleAudio}>
              <i
                className={`fa-solid fa-microphone-lines${
                  audio ? "" : "-slash"
                }`}
              ></i>
              <span>{audio ? "음소거" : "음소거 해제"}</span>
            </button> */}
            {/* 비디오 */}
            {/* <button
              title={`비디오${video ? "" : " 해제"}`}
              onClick={toggleVideo}
            >
              <i className={`fa-solid fa-video${video ? "" : "-slash"}`}></i>
              <span>{video ? "비디오" : "비디오 해제"}</span>
            </button> */}
          </div>
          {/* 비디오 메뉴 */}
          <div className="room_options">
            <button>
              <i className="fa-solid fa-user-plus"></i>
              <span>초대</span>
            </button>

            <button>
              <i className="fa-solid fa-desktop"></i>
              <span>화면공유</span>
            </button>

            <button onClick={() => setMenu((c) => ({ ...c, state: true }))}>
              <i className="fa-brands fa-elementor"></i>
              <span>메뉴</span>
            </button>
          </div>
          {/* 방 나가기 */}
          <button className="room_exit" onClick={leaveRoom}>
            방나가기
          </button>
        </footer>
      </div>

      {/* menu */}
      <div
        className="menu_container"
        style={{ display: menu.state ? "flex" : "none" }}
      >
        <button
          className="menu_close_btn"
          onClick={() => setMenu((c) => ({ ...c, state: false }))}
        ></button>
        <div className="pwf-streamRoom_menu">
          <div className="pwf-streamRoom_menu_btn_container">
            <div className="pwf-streamRoom_menu_btn_box">
              <button
                onClick={() => setMenu((c) => ({ ...c, type: "participants" }))}
              >
                참가자
              </button>
              <button onClick={() => setMenu((c) => ({ ...c, type: "chat" }))}>
                채팅
              </button>
            </div>
          </div>
          <ul className="pwf-streamRoom_menu_content">
            {menu.type == "chat" ? <Menuchat /> : <MenuParticipants />}
          </ul>
        </div>
      </div>
    </div>
  );
};
