// css
import "../css/signup.css";

// library
import { useRef, useState } from "react";
import { Form, Link } from "react-router-dom";

// component
import { SignupUser } from "../components/signupUser";

export const Signup = () => {
  let [SignupStep, setSignupStep] = useState<boolean>(false);

  return (
    <div className="page signup_page">
      <div className="signup_container" style={{ display: "none" }}>
        {/* 로그인 헤더 */}
        <header className="signup_header">
          <span>PWF</span>
        </header>
        {/* 로그인 내용 */}
        <div className="signup_content">
          <form action="post" onSubmit={submitUserInfo}>
            <div
              className="error_box"
              style={{ display: Error.state ? "flex" : "none" }}
            >
              {Error.message}
            </div>
            <input
              type="email"
              spellCheck={false}
              placeholder="이메일"
              ref={emailRef}
              value={Email}
              onChange={(e: InputChange) => setEmail(e.target.value)}
            />
            <input
              type="text"
              placeholder="닉네임"
              ref={nicknameRef}
              spellCheck={false}
              value={Nickname}
              onChange={(e: InputChange) => setNickname(e.target.value)}
            />
            <input
              type="password"
              placeholder="비밀번호"
              ref={passwordRef}
              value={Password}
              onChange={(e: InputChange) => setPassword(e.target.value)}
            />
            <input
              type="password"
              placeholder="비밀번호 확인"
              ref={passwordCheckRef}
              value={PasswordCheck}
              onChange={(e: InputChange) => setPasswordCheck(e.target.value)}
            />
            <button>회원가입</button>
          </form>
        </div>
        {/* 로그인 풋터 */}
        <footer className="signup_footer">
          <span>계정이 있다면?</span>
          <Link to="/login" className="login_anchor">
            로그인
          </Link>
        </footer>
      </div>

      <div className="signup_auth_box">
        <div className="signup_auth_header"></div>
        <div className="error_box"></div>
        <div className="signup_auth_content">
          <form method="post">
            <input type="number" />
          </form>
        </div>
      </div>
    </div>
  );
};
