import "../css/room/streamRoom.css";

import { useEffect, useRef, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { socketClient } from "../util/socket";
import { stream_service } from "../service/stream.service";
import { emitter } from "../util/event";
import { useSelector } from "react-redux";
import { RootState } from "../context/store";
import { Track } from "livekit-client";

// type
// import { Room } from "../types/room";
import { User } from "../types/user";
import { PeerConnects } from "../types/room";

// component
import { createFormData } from "../util/form";
import {
  ControlBar,
  GridLayout,
  LiveKitRoom,
  ParticipantTile,
  useTracks,
} from "@livekit/components-react";

export function TrackDisplay() {
  const tracks = useTracks([Track.Source.Camera, Track.Source.Microphone]);

  useEffect(() => {
    console.log(tracks);
  }, [tracks]);

  return <div>hi</div>;
}

export const StreamRoom = () => {
  // using
  let user = useSelector((state: RootState) => state.user);
  let { id } = useParams();
  let navigate = useNavigate();

  // let [room, setRoom] = useState < Room;
  let [token, setToken] = useState<string>("");
  let [url, setUrl] = useState<string>("");
  let [connect, setConnect] = useState<boolean>(false);

  // livekit token
  useEffect(() => {
    let start = async () => {
      try {
        let info = await stream_service.roomAccessToken(
          createFormData({ room: `room${id}`, identity: user.id })
        );
        setToken(info.jwt);
        setConnect(true);
      } catch (err) {
        console.log(err);
      }
    };
    start();
  }, []);

  // 메뉴 -----------------------------
  let [menu, setMenu] = useState<boolean>(false);

  return (
    <div className="page streamRoom_page">
      {/* 방 메인화면 */}

      <div>{token !== "" ? <p>hi염</p> : <p>no hi염</p>}</div>

      {/* 메뉴창 */}
      <div className="pwf-stream_menu">
        <button className="stream_menu_btn" onClick={() => setMenu((c) => !c)}>
          <i className={`fa-solid fa-chevron-${menu ? "right" : "left"}`}></i>
        </button>

        <ul
          className="stream_menu_content"
          style={{ width: menu ? "var(--stream-menu-size)" : "0px" }}
        >
          {/* 오디오 */}
          <button title="오디오">
            <i className="fa-solid fa-microphone-lines"></i>
          </button>
          {/* 비디오 */}
          <button title="비디오">
            <i className="fa-solid fa-video"></i>
          </button>
          {/* 방에 친구초대 */}
          <button title="초대">
            <i className="fa-solid fa-user-plus"></i>
          </button>
          {/* 화면공유 */}
          <button title="화면공유">
            <i className="fa-brands fa-creative-commons-share"></i>
          </button>
          {/* 내 미디어 변경 */}
          <button title="내 미디어">
            <i className="fa-solid fa-desktop"></i>
          </button>
          {/* 채팅 */}
          <button title="채팅">
            <i className="fa-solid fa-comments"></i>
          </button>
          {/* 참가자 */}
          <button title="참가자">
            <i className="fa-solid fa-user"></i>
          </button>

          {/* 방 나가기 */}
          <button className="room_exit" title="방 나가기">
            <i className="fa-solid fa-right-from-bracket"></i>
          </button>
        </ul>
      </div>
    </div>
  );
};
