import { useEffect, useState } from "react";
import "../css/authcode.css";
import { emitter } from "../util/event";
import { useNavigate, useParams } from "react-router-dom";
import { InputChange } from "../types/event";

export const Authcode = () => {
  let { email } = useParams();
  let navigate = useNavigate();
  let [state, setState] = useState<boolean>(false);

  let [authcode, setAuthcode] = useState<string>("");
  let [time, setTime] = useState<number>(180);

  useEffect(() => {
    const handler = (cur_state: boolean) => setState(cur_state);

    emitter.on("current state", handler);

    return () => {
      emitter.off("current state", handler);
    };
  }, []);

  useEffect(() => {
    if (!state) navigate(-1);
  }, [state]);

  const resend = async () => {};

  return (
    <div className="page authcode_page">
      <div className="authcode_text">
        <span>{email}으로 인증번호가 전송되었습니다.</span>
        <button onClick={resend} className="resend_btn">
          번호 재전송
        </button>
      </div>
      <div className="authcode_container">
        <div className="authcode_input_box">
          <input
            type="text"
            placeholder="인증번호"
            value={authcode}
            onChange={(e: InputChange) => setAuthcode(e.target.value)}
          />
          <span>03 : 00</span>
        </div>

        <button className="authcode_btn">인증번호 확인</button>
      </div>
    </div>
  );
};
