import "../css/friend.css";
import { useState, useEffect } from "react";
import { emitter } from "../util/event";

export const FriendModal = () => {
  let [open, setOpen] = useState<boolean>(true);

  useEffect(() => {
    const handler = (open: boolean) => setOpen(open);
    emitter.on("friend modal", handler);

    return () => {
      emitter.off("friend modal", handler);
    };
  }, []);

  return (
    <div className="modal_box" style={{ display: open ? "flex" : "none" }}>
      <div className="pwf_friend_modal">
        {/* 친추요청 검색 */}
        <div className="friend_search_box">
          <form className="friend_search_form">
            <input
              type="text"
              placeholder="친구요청 닉네임"
              className="friend_search_input"
            />
          </form>
        </div>
        {/* 친추 리스트 */}
        <div className="friend_request_box"></div>
      </div>
    </div>
  );
};
