// css
import "../css/signup.css";

// library
import { useRef, useState } from "react";
import { Form, Link } from "react-router-dom";

// other file
import { createFormData } from "../util/form";
import { user_service } from "../service/userservice";
import { emailValidate, passwordValidate } from ".././util/validate";

// type
import { InputChange, FormSubmit } from "../types/event";
import { User } from "../../../types/user";

interface ErrorProps {
  state: boolean;
  message: string;
}

export const Signup = () => {
  let [Error, setError] = useState<ErrorProps>({ state: false, message: "" });
  let [SignupStep, setSignupStep] = useState<boolean>(false);

  const ErrorHandler = (message: string) =>
    setError((c) => ({ ...c, state: true, message }));

  const submitUserInfo = async (e: FormSubmit) => {
    e.preventDefault();

    if (!emailValidate(Email)) {
      ErrorHandler("이메일 형식이 올바르지 않습니다");
      if (emailRef.current) emailRef.current.focus();
      return;
    }
    if (!passwordValidate(Password)) {
      ErrorHandler(
        "비밀번호는 영문자, 숫자, 특수문자를 모두 포함하여 8~20자 이내여야 합니다"
      );
      if (passwordRef.current) passwordRef.current.focus();
      return;
    }
    if (Password !== PasswordCheck) {
      ErrorHandler("비밀번호와 비밀번화 확인값이 다릅니다");
      if (passwordCheckRef.current) passwordCheckRef.current.focus();
      return;
    }

    setSignupStep(true);

    // 나중에 form형식 만들기
    // let info = {
    //   email: Email,
    //   nickname: Nickname,
    //   password: Password,
    //   password_check: PasswordCheck,
    // };
    // let data = createFormData(info);
    // let formData = new FormData();

    // formData.append("email", Email);
    // formData.append("nickname", Nickname.trim());
    // formData.append("password", Password);
    // formData.append("password_check", PasswordCheck);

    // try {
    //   let res = await user_service.createUser(formData);
    //   console.log(res);
    // } catch (err) {
    //   alert(err);
    // }
  };

  return (
    <div className="page signup_page">
      <div className="signup_container" style={{ display: "none" }}>
        {/* 로그인 헤더 */}
        <header className="signup_header">
          <span>PWF</span>
        </header>
        {/* 로그인 내용 */}
        <div className="signup_content">
          <form action="post" onSubmit={submitUserInfo}>
            <div
              className="error_box"
              style={{ display: Error.state ? "flex" : "none" }}
            >
              {Error.message}
            </div>
            <input
              type="email"
              spellCheck={false}
              placeholder="이메일"
              ref={emailRef}
              value={Email}
              onChange={(e: InputChange) => setEmail(e.target.value)}
            />
            <input
              type="text"
              placeholder="닉네임"
              ref={nicknameRef}
              spellCheck={false}
              value={Nickname}
              onChange={(e: InputChange) => setNickname(e.target.value)}
            />
            <input
              type="password"
              placeholder="비밀번호"
              ref={passwordRef}
              value={Password}
              onChange={(e: InputChange) => setPassword(e.target.value)}
            />
            <input
              type="password"
              placeholder="비밀번호 확인"
              ref={passwordCheckRef}
              value={PasswordCheck}
              onChange={(e: InputChange) => setPasswordCheck(e.target.value)}
            />
            <button>회원가입</button>
          </form>
        </div>
        {/* 로그인 풋터 */}
        <footer className="signup_footer">
          <span>계정이 있다면?</span>
          <Link to="/login" className="login_anchor">
            로그인
          </Link>
        </footer>
      </div>

      <div className="signup_auth_box">
        <div className="signup_auth_header"></div>
        <div className="error_box"></div>
        <div className="signup_auth_content">
          <form method="post">
            <input type="number" />
          </form>
        </div>
      </div>
    </div>
  );
};
