import { useState } from "react";

import { emitter } from "../../util/event";

// type
import { User } from "../../types/user";
import { ModalList } from "../../page/modal";

interface InvitationProps {
  user: User;
  type: ModalList;
}

// 친구 관련 모달
export const Invitation = ({ user, type }: InvitationProps) => {
  function resetModal() {
    emitter.emit("modal", { type });
  }

  return (
    <div
      className="invitation_modal"
      style={{ display: type == "invitation" ? "flex" : "none" }}
    >
      <header className="modal_header">
        <button onClick={resetModal}>X</button>
      </header>
      <ul className="invitation_list"></ul>
    </div>
  );
};
