import { Room } from "livekit-client";
import { createContext, ReactNode, useContext, useMemo, useState } from "react";
import { TrackProps, UserTrackProps } from "../types/stream.types";

let StreamContext = createContext<any>(undefined);
let SetStreamContext = createContext<any>(undefined);

export const StreamProvider = ({ children }: { children: ReactNode }) => {
  const [room, setRoom] = useState<any>(null);
  let [participants, setParticipants] = useState<UserTrackProps>({});

  let value = useMemo(
    () => ({
      room,
      participants,

      setRoom,
      setParticipants,
    }),
    [room, participants]
  );

  return (
    <StreamContext.Provider value={value}>
      <SetStreamContext.Provider value={setvalue}></SetStreamContext.Provider>
    </StreamContext.Provider>
  );
};

export let useStream = () => {
  const context = useContext(StreamContext);
  if (context === undefined) throw new Error("value가 없다고");
  return context;
};
