import { useEffect, useRef, useState } from "react";
import "../css/streamRoom.css";
import { useNavigate, useParams } from "react-router-dom";
import { socketClient } from "../util/socket";
import { prefetchDNS } from "react-dom";

export const StreamRoom = () => {
  let { id } = useParams();
  let navigate = useNavigate();

  // ref
  let pcRef = useRef<any>(null); // 내 peer 정보
  let videoRef = useRef<HTMLVideoElement | null>(null);
  let streamRef = useRef<MediaStream | null>(null);

  // state
  let [menu, setMenu] = useState<boolean>(true); //메뉴
  let [audio, setAudio] = useState<boolean>(true); //오디오
  let [video, setVideo] = useState<boolean>(true); //비디오

  const [connectList, setConnectList] = useState<
    { nickname: string; stream: MediaStream }[]
  >([]);

  // peer rtc작업
  let peerConnect = async () => {
    let pc = new RTCPeerConnection();

    pc.onicecandidate = (event) => {
      // console.log(event);
      if (event.candidate) socketClient.emit("candidate", id, event.candidate);
    };

    pc.ontrack = (event) => {
      // console.log(event);
    };
    return pc;
  };
  // 화면 on/off
  let toggleVideo = async () => {
    if (!streamRef.current) return;

    streamRef.current.getVideoTracks().forEach((track) => {
      track.enabled = !track.enabled;
    });
    setVideo(!video);
  };
  // 오디오 on/offs
  let toggleAudio = async () => {
    if (!streamRef.current) return;

    streamRef.current.getAudioTracks().forEach((track) => {
      track.enabled = !track.enabled;
    });
    setAudio(!audio);
  };
  // 방을 떠남
  let leaveRoom = () => {
    navigate("/");
    socketClient.emit("leave room", `room${id}`);
  };

  // 시작시 signalling
  useEffect(() => {
    // 룸에 입장
    socketClient.emit("join room", `room${id}`);

    socketClient.on("someone join", async (text: string) => {
      console.log(text);
    });

    // signalling offer
    socketClient.on("offer", async (offer: RTCSessionDescriptionInit) => {
      try {
        // 상대 정보를 수용하고
        await pcRef.current.setRemoteDescription(offer);

        const answer: RTCSessionDescriptionInit =
          await pcRef.current.createAnswer();
        pcRef.current.setLocalDescription(answer);
        socketClient.emit("answer", `room${id}`, answer);
      } catch (err) {
        console.log("signalling offer error:", err);
      }
    });
    // signalling answer
    socketClient.on("answer", async (answer: RTCSessionDescriptionInit) => {
      try {
        console.log(answer);
        // await pcRef.current.setRemoteDescription(answer);
      } catch (err) {
        console.log("signalling answer error:", err);
      }
    });
    // signalling candidate
    socketClient.on("candidate", async () => {});
  }, []);

  // 시작시 내 스트림
  useEffect(() => {
    // 유저 미디오 가져오기
    const call = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({
          video,
          audio,
        });
        streamRef.current = stream;
        if (videoRef.current) videoRef.current.srcObject = stream;

        pcRef.current = await peerConnect();

        stream.getTracks().forEach((track) => {
          pcRef.current.addTrack(track);
        });
        let offer: RTCSessionDescriptionInit =
          await pcRef.current.createOffer();
        console.log(offer);
        await pcRef.current.setLocalDescription(offer);
        socketClient.emit("offer", `room${id}`, offer);
      } catch (err) {
        console.log(err);
      }
    };
    call();
  }, []);

  return (
    <div className="page streamRoom_page">
      {/* main */}
      <div className="pwf-streamRoom_container">
        {/* 화면창 */}
        <ul className="user_screen_lists">
          <video
            ref={videoRef}
            autoPlay
            playsInline
            className="user_video"
          ></video>
        </ul>
        {/* main footer */}
        <footer className="streamRoom_footer">
          {/* 유저 음성 및 비디오 */}
          <div className="user_video_options">
            {/* 음성 */}
            <button
              title={`음소거${audio ? "" : " 해제"}`}
              onClick={toggleAudio}
            >
              <i
                className={`fa-solid fa-microphone-lines${
                  audio ? "" : "-slash"
                }`}
              ></i>
            </button>
            {/* 비디오 */}
            <button
              title={`비디오${video ? "" : " 해제"}`}
              onClick={toggleVideo}
            >
              <i className={`fa-solid fa-video${video ? "" : "-slash"}`}></i>
            </button>
          </div>
          {/* 비디오 메뉴 */}
          <div className="room_options">
            <button title="친구초대">
              <i className="fa-solid fa-user-plus"></i>
            </button>

            <button title="화면공유">
              <i className="fa-solid fa-desktop"></i>
            </button>
          </div>
          {/* 방 나가기 */}
          <button className="room_exit" onClick={leaveRoom}>
            방나가기
          </button>
        </footer>
      </div>

      {/* menu */}
      <div className="pwf-streamRoom_menu">
        <div className="pwf-streamRoom_menu_btn_container">
          <div className="pwf-streamRoom_menu_btn_box">
            <button>참가자</button>
            <button>채팅</button>
          </div>
        </div>
        <ul className="pwf-streamRoom_menu_content"></ul>
      </div>
    </div>
  );
};
