import { Dispatch } from "react";

interface InputProps {
  error: boolean;
  error_msg: string;
  value: string;
}

// 이메일 인가
export const emailValidate = async (
  email: string,
  set_email: Dispatch<React.SetStateAction<InputProps>>
) => {
  let result = email
    .toString()
    .trim()
    .toLowerCase()
    .match(
      /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|.(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
    );
};

// 비밀번호 인가
export const passwordValidate = (
  password: string,
  set_password: () => void
) => {
  return password
    .trim()
    .toLowerCase()
    .match(/^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!*?&])[A-Za-z\d@$!%*?&]{8,20}$/);
};
