import { useEffect, useRef, useState } from "react";
import "../css/streamRoom.css";
import { useNavigate, useParams } from "react-router-dom";
import { socketClient } from "../util/socket";
import { User } from "../types/user";
import { stream_service } from "../service/streamservice";
import { Room } from "../types/room";
import { off } from "process";
import { userAction } from "../context/actions/userAction";
import { useSelector } from "react-redux";
import { RootState } from "../context/store";

interface UserStream {
  from: string;
  stream: MediaStream;
}

export const MenuParticipants = () => {
  return <div>participants</div>;
};

export const Menuchat = () => {
  return <div>chat</div>;
};

export const StreamBox = ({ from, stream }: UserStream) => {
  let videoRef = useRef<HTMLVideoElement | null>(null);

  useEffect(() => {
    videoRef.current!.srcObject = stream;
  }, []);
  return (
    <div className="user_video_box">
      <video ref={videoRef} autoPlay playsInline className="user_video"></video>
      <div className="user_nickname">{from}</div>
    </div>
  );
};

export const StreamRoom = () => {
  let user = useSelector((state: RootState) => state.user);
  let { id } = useParams();
  let navigate = useNavigate();

  // ref
  let localRef = useRef<HTMLVideoElement | null>(null);
  let localStreamRef = useRef<MediaStream | null>(null);
  let peerConnects = useRef<{ [nickname: string]: RTCPeerConnection }>({});

  // state
  let [connectList, setConnectList] = useState<UserStream[]>([]);
  let [room, setRoom] = useState<Room>({});
  let [menu, setMenu] = useState<{
    state: boolean;
    type: "participants" | "chat";
  }>({ state: false, type: "participants" }); //메뉴
  let [audio, setAudio] = useState<boolean>(true); //오디오
  let [video, setVideo] = useState<boolean>(true); //비디오

  // function ----------------------------

  // connect 연결
  let peerConnect = async (to: string, stream?: MediaStream) => {
    let pc = new RTCPeerConnection();

    stream?.getTracks().forEach((track) => {
      pc.addTrack(track, stream);
    });

    pc.onicecandidate = (e) => {
      if (e.candidate)
        socketClient.emit("candidate", user.nickname, to, e.candidate);
    };

    pc.ontrack = (e) => {
      let kind = e.track.kind;

      setConnectList((c) =>
        c.map((val) => {
          return val.from == to
            ? { ...val, stream: val.stream.addTrack(e.track) }
            : val;
        })
      );

      // setConnectList((c) => [...c, { from: to, stream: e }]);
    };

    let offer = await pc.createOffer();
    await pc.setLocalDescription(offer!);
    socketClient.emit("offer", user.nickname, to, offer);

    peerConnects.current[to] = pc;
  };

  // 연결 시작
  let start = async () => {
    try {
      let stream = await navigator.mediaDevices.getUserMedia({ video, audio });
      localRef.current!.srcObject = stream;
      localStreamRef.current = stream;

      // 트랙을 더해줌
      room.participants?.map(async (val: any) => {
        if (val.nickname !== user.nickname)
          await peerConnect(val.nickname, stream);
        setConnectList((c) => [
          ...c,
          { from: val.nickname, stream: new MediaStream() },
        ]);
      });
    } catch (err) {
      console.log(err);
    }
  };

  // offer처리
  let offerHandler = async (from: string, offer: RTCSessionDescriptionInit) => {
    try {
      if (!peerConnects.current[from]) peerConnect(from);

      await peerConnects.current[from]?.setRemoteDescription(
        new RTCSessionDescription(offer)
      );

      if (
        peerConnects.current[from]?.signalingState == "have-remote-offer" &&
        user.nickname !== ""
      ) {
        const answer = await peerConnects.current[from]?.createAnswer();
        await peerConnects.current[from]?.setLocalDescription(answer!);
        socketClient.emit("answer", user.nickname, from, answer);
      }
    } catch (err) {
      console.log(err);
    }
  };

  // answer 처리
  let answerHandler = async (
    from: string,
    answer: RTCSessionDescriptionInit
  ) => {
    try {
      if (!peerConnects.current[from]?.currentRemoteDescription)
        await peerConnects.current[from]?.setRemoteDescription(answer!);
    } catch (err) {
      console.log(err);
    }
  };

  // candidate처리
  let candidateHandler = async (from: string, candidate: RTCIceCandidate) => {
    const iceCandidate = new RTCIceCandidate(candidate);
    await peerConnects.current[from]?.addIceCandidate(iceCandidate);
  };

  // 화면 on/off
  let toggleMedia = (type: "audio" | "video") => {
    if (!localStreamRef.current) return;

    localStreamRef.current
      .getTracks()
      .filter((track) => track.kind === type)
      .forEach((track) => {
        track.enabled = !track.enabled;
      });

    type == "audio" ? setAudio(!audio) : setVideo(!video);
  };

  // room leave 처리
  let leaveRoomHandler = (who: string) => {
    delete peerConnects.current[who];
    setConnectList((c) => c.filter((val) => val.from !== who));
  };

  // 방을 떠남
  let leaveRoom = () => {
    if (user.nickname !== "") {
      socketClient.emit("leave room", user.nickname, `room${id}`);
      navigate("/");
    }
  };

  // useEffect -----------------------------

  // 시작시 방 정보 얻어옴
  useEffect(() => {
    let data = async () => {
      try {
        let room = await stream_service.getStreamRoomData(id!);
        setRoom(room.room);
      } catch (err) {
        console.log(err);
      }
    };
    data();
  }, []);

  // 룸정보가 오면 start시작
  useEffect(() => {
    if (room.participants?.length) {
      room.participants?.map((val: any) => {
        if (val.nickname !== user.nickname) peerConnect(val.nickname);
      });
      start();
    }
  }, [room]);

  // 소켓 연결
  useEffect(() => {
    socketClient.emit("join", `room${id}`);

    socketClient.on("offer", offerHandler); //signaling offer
    socketClient.on("answer", answerHandler); //signaling answer
    socketClient.on("candidate", candidateHandler); //signaling candidate
    socketClient.on("leave room", leaveRoomHandler);

    return () => {
      socketClient.off("offer", offerHandler);
      socketClient.off("answer", answerHandler);
      socketClient.off("candidate", candidateHandler);
      socketClient.on("leave room", leaveRoomHandler);
    };
  }, []);

  return (
    <div className="page streamRoom_page">
      {/* main */}
      <div className="pwf-streamRoom_container">
        {/* 화면창 */}
        <ul className="user_screen_lists">
          <div className="user_video_box">
            <video
              ref={localRef}
              autoPlay
              playsInline
              className="user_video"
            ></video>
            <div className="user_nickname">{user.nickname!}</div>
          </div>
          {/* 다른 참가자 스트리밍 */}
          {connectList.length &&
            connectList.map((val, idx) => (
              <StreamBox key={idx} from={val.from} stream={val.stream} />
            ))}
        </ul>
        {/* main footer */}
        <footer className="streamRoom_footer">
          {/* 유저 음성 및 비디오 */}
          <div className="user_video_options">
            {/* 음성 */}
            <button onClick={() => toggleMedia("audio")}>
              <i
                className={`fa-solid fa-microphone-lines${
                  audio ? "" : "-slash"
                }`}
              ></i>
              <span>{audio ? "음소거" : "음소거 해제"}</span>
            </button>
            {/* 비디오 */}
            <button
              title={`비디오${video ? "" : " 해제"}`}
              onClick={() => toggleMedia("video")}
            >
              <i className={`fa-solid fa-video${video ? "" : "-slash"}`}></i>
              <span>{video ? "비디오" : "비디오 해제"}</span>
            </button>
          </div>
          {/* 비디오 메뉴 */}
          <div className="room_options">
            <button>
              <i className="fa-solid fa-user-plus"></i>
              <span>초대</span>
            </button>

            <button>
              <i className="fa-solid fa-desktop"></i>
              <span>화면공유</span>
            </button>

            <button onClick={() => setMenu((c) => ({ ...c, state: true }))}>
              <i className="fa-brands fa-elementor"></i>
              <span>메뉴</span>
            </button>
          </div>
          {/* 방 나가기 */}
          <button className="room_exit" onClick={leaveRoom}>
            방나가기
          </button>
        </footer>
      </div>

      {/* menu */}
      <div
        className="menu_container"
        style={{ display: menu.state ? "flex" : "none" }}
      >
        <button
          className="menu_close_btn"
          onClick={() => setMenu((c) => ({ ...c, state: false }))}
        ></button>
        <div className="pwf-streamRoom_menu">
          <div className="pwf-streamRoom_menu_btn_container">
            <div className="pwf-streamRoom_menu_btn_box">
              <button
                onClick={() => setMenu((c) => ({ ...c, type: "participants" }))}
              >
                참가자
              </button>
              <button onClick={() => setMenu((c) => ({ ...c, type: "chat" }))}>
                채팅
              </button>
            </div>
          </div>
          <ul className="pwf-streamRoom_menu_content">
            {menu.type == "chat" ? <Menuchat /> : <MenuParticipants />}
          </ul>
        </div>
      </div>
    </div>
  );
};
