import { useLocation, useNavigate, useParams } from "react-router-dom";
import { useEffect, useState } from "react";
// css
import "../css/modal/authcode.css";

// service
import { auth_service } from "../../service/auth.service";
import { user_service } from "../../service/user.service";
// util
import { createFormData } from "../../util/form";

// type
import { User } from "../../types/user";
import { InputChange } from "../../types/event";
import { ModalList } from "../../page/modal";

interface AuthcodeProps {
  user: User;
  type: ModalList;
  callback?: () => void;
}

export const Authcode = ({ user, type, callback }: AuthcodeProps) => {
  let { email } = useParams();
  let navigate = useNavigate();
  let location = useLocation();

  let [authcode, setAuthcode] = useState<string>("");
  let [time, setTime] = useState<number>(180);

  const authcodeCheck = async () => {
    try {
      if (callback) callback();
    } catch (err) {
      console.log(err);
    }
  };

  // 전 과정이 없이 이 페이지에 도달하면 그냥 전 페이지로 이동시켜버림
  useEffect(() => {
    if (!location.state.state) navigate(-1);
  }, []);

  const resend = async () => {
    try {
      let { message } = await auth_service.resendAuthcode(
        createFormData({ email })
      );
      alert(message);
      setTime(180);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="page authcode_page">
      <div className="authcode_text">
        <span>{email}으로 인증번호가 전송되었습니다.</span>
        <button onClick={resend} className="resend_btn">
          번호 재전송
        </button>
      </div>
      <div className="authcode_container">
        <div className="authcode_input_box">
          <input
            type="text"
            placeholder="인증번호"
            value={authcode}
            onChange={(e: InputChange) => setAuthcode(e.target.value)}
          />
          <span>{time}</span>
        </div>

        <button className="authcode_btn" onClick={authcodeCheck}>
          인증번호 확인
        </button>

        <button className="backpage_btn">돌아가기</button>
      </div>
    </div>
  );
};
