import { useEffect, useState } from "react";

import { emitter } from "../../util/event";

// css
import "../../css/room/menu.css";

// component
import { Chat } from "./chat";
import { Participants } from "./participants";
import { User } from "../../types/user";
import { Room } from "../../types/room";

export const Menu = () => {
  let [menu, setMenu] = useState<boolean>(true);
  return (
    <div className="pwf-stream_menu">
      <button className="stream_menu_btn">
        <i className="fa-solid fa-chevron-left"></i>
      </button>

      <ul className="stream_menu_content">
        <button>
          <i className="fa-solid fa-microphone-lines"></i>
        </button>
        {/* 비디오 */}
        <button>
          <i className="fa-solid fa-video"></i>
        </button>
        {/* 방에 친구초대 */}
        <button>
          <i className="fa-solid fa-user-plus"></i>
        </button>
        {/* 화면공유 */}
        <button>
          <i className="fa-brands fa-creative-commons-share"></i>
        </button>
        {/* 내 미디어 변경 */}
        <button>
          <i className="fa-solid fa-desktop"></i>
        </button>
        {/* 메뉴 */}
        <button>
          <i className="fa-brands fa-elementor"></i>
        </button>
        <button className="room_exit">
          <i className="fa-solid fa-right-from-bracket"></i>
        </button>
      </ul>
    </div>
  );
};
