// css
import { profile } from "console";
import "../css/pageheader.css";

import React, { memo, useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { userInit } from "../context/reducer/userReducer";

import { emitter } from "../util/event";
import { useSelector } from "react-redux";
import { AppDispatch, persistor, RootState } from "../context/store";
import { user_service } from "../service/userservice";
import { socketClient } from "../util/socket";
import { useDispatch } from "react-redux";

export const PageHeader = () => {
  let navigate = useNavigate();
  let user = useSelector((state: RootState) => state.user);
  let dispatch = useDispatch<AppDispatch>();
  let [userOption, setUserOption] = useState<boolean>(false);

  const logout = async () => {
    try {
      await user_service.logout();
      dispatch(userInit());
      await persistor.purge();
      socketClient.disconnect();
      navigate("/login");
    } catch (err) {
      console.log(err);
      throw err;
    }
  };

  return (
    <header className="pwf_header">
      <span>
        <Link to="/" className="home_anchor">
          PWF {user.nickname}
        </Link>
      </span>

      <div className="header_control">
        {/* 친구추가 */}
        <div className="pwf_friend_box">
          <button
            className="friend_btn"
            onClick={() =>
              emitter.emit("modal", { type: "friend", open: true })
            }
          >
            <i className="fa-solid fa-user-plus"></i>
          </button>

          <span
            className="friend_add_number"
            style={{
              display:
                user.request_friends && user.request_friends.length
                  ? "flex"
                  : "none",
            }}
          >
            {user.request_friends && user.request_friends.length
              ? user.request_friends.length
              : ""}
          </span>
        </div>

        {/* 유저 프로필 */}
        <div className="pwf_user_box">
          <button
            className="pwf_user_btn"
            onFocus={() => setUserOption(true)}
            onBlur={() => setUserOption(false)}
          >
            <img src={user.profile_img} />
          </button>

          <div
            className="check_box"
            style={{ display: userOption ? "flex" : "none" }}
          >
            <button
              onMouseDown={() =>
                emitter.emit("modal", { type: "profile", open: true })
              }
            >
              <i className="fa-solid fa-user"></i>
              <span>프로필</span>
            </button>
            <button onMouseDown={logout}>
              <i className="fa-solid fa-right-from-bracket"></i>
              <span>로그아웃</span>
            </button>
          </div>
        </div>
      </div>
    </header>
  );
};
