// css
import "../css/home.css";

import { emitter } from "../util/event";
import { AppDispatch, RootState } from "../context/store";
import { useDispatch, useSelector } from "react-redux";
import { userAction } from "../context/actions/userAction";

// component
import { PageHeader } from "../components/pageHeader";
import { StreamModal } from "../components/streamRoom";
import { Profile } from "../components/profile";
import { useEffect, useMemo } from "react";

export const Home = () => {
  let dispatch = useDispatch<AppDispatch>();
  let user = useSelector((state: RootState) => state.user);

  useMemo(() => {
    dispatch(userAction.getUserAction());
  }, [dispatch]);

  return (
    <div className="page home_page">
      {/* header */}
      <PageHeader />

      {/* streamroom modal */}
      <StreamModal />
      <Profile />

      {/* content */}
      <div className="pwf_content">
        <ul className="stream_room_lists">
          <button onClick={() => emitter.emit("stream modal", true)}>
            방만들기
          </button>
        </ul>
        <div className="pwf_menu"></div>
      </div>
    </div>
  );
};
