import "../../css/modal/invitation.css";

import { useEffect, useState } from "react";

import { emitter } from "../../util/event";

// type
import { User } from "../../types/user";
import { Room } from "../../types/room";

interface InvitationProps {
  user: User;
  participants: Room["participants"];
  show: boolean;
  setShow: any;
}

const InvitationLi = ({ user, participants }: InvitationProps) => {
  return (
    <li className="invitation_li">
      {/* 유저 정보 */}
      <div>
        <span className="invitation_profile_box">
          <img src={user.profile_img} />
        </span>
        <span className="invitation_nickname">{user.nickname}</span>
      </div>
      {/* 초대버튼 */}
      <button>초대</button>
    </li>
  );
};

// 친구 관련 모달
export const Invitation = ({
  user,
  show,
  setShow,
  participants,
}: InvitationProps) => {
  function reset() {
    emitter.emit("modal", { type: "invitation" });
  }

  useEffect(() => {
    setShow(false);
    console.log(participants, user.friends);
  }, [participants]);

  return (
    <div
      className="invitation_modal"
      style={{ display: show ? "flex" : "none" }}
    >
      <header className="modal_header">
        <button onClick={reset}>X</button>
      </header>

      <ul className="invitation_list"></ul>
    </div>
  );
};
