import "../css/room/streamRoom.css";

import React, { useEffect, useRef, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { socketClient } from "../util/socket";
import { stream_service } from "../service/stream.service";
import { emitter } from "../util/event";
import { useSelector } from "react-redux";
import { RootState } from "../context/store";
import {
  Track,
  Room,
  RemoteParticipant,
  RemoteTrackPublication,
  createLocalTracks,
  LocalVideoTrack,
  LocalAudioTrack,
  RemoteVideoTrack,
  RemoteAudioTrack,
} from "livekit-client";

// type
// import { Room } from "../types/room";
import { User } from "../types/user";
import { PeerConnects } from "../types/room";

// component
import { createFormData } from "../util/form";
import {
  connectRoom,
  getMediaStream,
  getStream,
  getTrack,
} from "../util/stream";
import { TrackProps } from "../types/stream.types";

interface ParticipantProps {
  nickname: string;
  trackInfo: TrackProps;
}

const ParticipantVideo = ({ nickname, trackInfo }: ParticipantProps) => {
  let videoRef = useRef<HTMLVideoElement | null>(null);

  useEffect(() => {
    if (!videoRef.current) return;
    trackInfo.audio_track?.attach(videoRef.current);
    trackInfo.video_track?.attach(videoRef.current);
  }, [trackInfo.audio_track, trackInfo.video_track]);
  return (
    <div className="participant_track_box">
      <video ref={videoRef} autoPlay></video>
      <div className="participant_infomation">
        <span>
          <i
            className={`fa-solid fa-microphone${
              trackInfo.audio_state ? "" : "-slash"
            }`}
          ></i>
        </span>
        <span>
          <i
            className={`fa-solid fa-video${
              trackInfo.video_state ? "" : "-slash"
            }`}
          ></i>
        </span>
        <span>{nickname}</span>
      </div>
    </div>
  );
};

export const StreamRoom = () => {
  // using
  let user = useSelector((state: RootState) => state.user);
  let { id } = useParams();
  let navigate = useNavigate();
  let [token, setToken] = useState<string>("");

  // menu
  let [menu, setMenu] = useState<boolean>(true);

  // pariticipant
  let [participant, setParticipant] = useState<{
    [nickname: string]: TrackProps;
  }>({});

  // track
  let localRef = useRef<HTMLVideoElement | null>(null);
  let videoTrack = useRef<LocalVideoTrack | null>(null);
  let audioTrack = useRef<LocalAudioTrack | null>(null);

  let testRef = useRef<HTMLVideoElement | null>(null);

  let [audio, setAudio] = useState<boolean>(true);
  let [video, setVideo] = useState<boolean>(true);

  // share
  let [share, setShare] = useState<boolean>(false);

  // init ------------------------------------------
  useEffect(() => {
    let start = async () => {
      try {
        let token = await stream_service.roomAccessToken(
          createFormData({ room: `room${id}`, identity: user.nickname })
        );
        setToken(token);
      } catch (err) {
        console.log(err);
      }
    };
    start();
  }, []);

  useEffect(() => {
    let test = async () => {
      try {
        const room = await connectRoom(token);
        const tracks = await getStream(audio, video);

        videoTrack.current = getTrack(tracks!, "video") as LocalVideoTrack;
        audioTrack.current = getTrack(tracks!, "audio") as LocalAudioTrack;

        if (videoTrack.current && localRef.current) {
          await room.localParticipant.publishTrack(videoTrack.current);
          videoTrack.current.attach(localRef.current);
        }
        if (audioTrack.current && localRef.current) {
          await room.localParticipant.publishTrack(audioTrack.current);
          audioTrack.current.attach(localRef.current);
        }

        // 새 참가자 관련
        room.on("participantConnected", (participant: RemoteParticipant) => {
          let identity = participant.identity;
          setParticipant((c) => ({ ...c, [identity]: {} }));

          participant.on("trackSubscribed", (track) => {
            track.kind == "video"
              ? setParticipant((c) => ({
                  ...c,
                  [identity]: {
                    ...c[identity],
                    video_state: true,
                    video_track: track as RemoteVideoTrack,
                  },
                }))
              : setParticipant((c) => ({
                  ...c,
                  [identity]: {
                    ...c[identity],
                    audio_state: true,
                    audio_track: track as RemoteAudioTrack,
                  },
                }));
          });

          participant.on("trackMuted", (track) => {
            setParticipant((c) => ({
              ...c,
              [identity]: {
                ...c[identity],
                video_state:
                  track.kind == "video" ? false : c[identity].video_state,
                audio_state:
                  track.kind == "audio" ? false : c[identity].audio_state,
              },
            }));
          });

          participant.on("trackUnmuted", (track) => {
            setParticipant((c) => ({
              ...c,
              [identity]: {
                ...c[identity],
                video_state:
                  track.kind == "video" ? true : c[identity].video_state,
                audio_state:
                  track.kind == "audio" ? true : c[identity].audio_state,
              },
            }));
          });

          // participant.on("track");
        });

        // 기준 참가자 관련
        room.remoteParticipants.forEach((participant) => {
          let identity = participant.identity;

          participant.getTrackPublications().forEach((pub) => {
            if (pub.isSubscribed && pub.track) {
              pub.kind == "video"
                ? setParticipant((c) => ({
                    ...c,
                    [identity]: {
                      ...c[identity],
                      video_state: true,
                      video_track: pub.track as RemoteVideoTrack,
                    },
                  }))
                : setParticipant((c) => ({
                    ...c,
                    [identity]: {
                      ...c[identity],
                      audio_state: true,
                      audio_track: pub.track as RemoteAudioTrack,
                    },
                  }));
            }
          });
          participant.on("trackMuted", (pub) => {
            setParticipant((c) => ({
              ...c,
              [identity]: {
                ...c[identity],
                video_state:
                  pub.kind == "video" ? false : c[identity].video_state,
                audio_state:
                  pub.kind == "audio" ? false : c[identity].audio_state,
              },
            }));
          });

          participant.on("trackUnmuted", (pub) => {
            setParticipant((c) => ({
              ...c,
              [identity]: {
                ...c[identity],
                video_state:
                  pub.kind == "video" ? true : c[identity].video_state,
                audio_state:
                  pub.kind == "audio" ? true : c[identity].audio_state,
              },
            }));
          });
        });
      } catch (err) {
        console.log(err);
      }
    };

    if (token !== "") test();

    return () => {
      videoTrack.current?.detach();
      videoTrack.current?.stop();
      audioTrack.current?.stop();
      // room?
    };
  }, [token]);

  // track
  const toggleTrack = (type: "audio" | "video") => {
    if (!localRef.current) return;
    if (type == "video") {
      if (!videoTrack.current) return;

      video ? videoTrack.current.mute() : videoTrack.current.unmute();
      setVideo((c) => !c);
    } else {
      if (!audioTrack.current) return;

      audio ? audioTrack.current.mute() : audioTrack.current.unmute();
      setAudio((c) => !c);
    }
  };

  // share
  function sharing() {
    setShare((c) => !c);
  }

  return (
    <div className="page streamRoom_page">
      {/* <video className="pppp" src="#" ref={localRef} autoPlay></video> */}
      {/* 방 메인화면 */}

      {/* 참가자들의 화면 부분 */}
      <div className="pwf-stream_container">
        {/* 참가자들 비디오 부분들 */}
        <div className="pwf-stream_screen_section">
          {/* 참가자 화면 */}
          <ul className="participant_stream_container">
            <div className="participant_track_box">
              <video ref={localRef} autoPlay></video>
              <div className="participant_infomation">
                <span>
                  <i
                    className={`fa-solid fa-microphone${audio ? "" : "-slash"}`}
                  ></i>
                </span>
                <span>
                  <i
                    className={`fa-solid fa-video${video ? "" : "-slash"}`}
                  ></i>
                </span>
                <span>{user.nickname}</span>
              </div>
            </div>

            {Object.entries(participant).map(([nickname, value]) => (
              <ParticipantVideo nickname={nickname} trackInfo={value} />
            ))}
          </ul>
          {/* 공유 화면 */}
          <div
            className="share_stream_container"
            style={{ display: share ? "flex" : "none" }}
          >
            <div className="share_screen_box">
              {/* <video ref={localRef} className="share_test" autoPlay></video> */}
              <span className="share_user">administer</span>
            </div>
          </div>
        </div>
        {/* 풋터 */}
        <footer className="pwf-stream_footer">
          {/* 오디오 */}
          <button title="오디오" onClick={() => toggleTrack("audio")}>
            <i className={`fa-solid fa-microphone${audio ? "" : "-slash"}`}></i>
            <span>오디오</span>
          </button>
          {/* 비디오 */}
          <button title="비디오" onClick={() => toggleTrack("video")}>
            <i className={`fa-solid fa-video${video ? "" : "-slash"}`}></i>
            <span>비디오</span>
          </button>
          {/* 방에 친구초대 */}
          <button title="초대">
            <i className="fa-solid fa-user-plus"></i>
            <span>초대</span>
          </button>
          {/* 화면공유 */}
          <button onClick={sharing} title="화면공유">
            <i className="fa-brands fa-creative-commons-share"></i>
            <span>화면공유</span>
          </button>
          {/* 내 미디어 변경 */}
          <button title="내 미디어">
            <i className="fa-solid fa-desktop"></i>
            <span>내미디어</span>
          </button>
          {/* 채팅 */}
          <button title="채팅">
            <i className="fa-solid fa-comments"></i>
            <span>채팅</span>
          </button>
          {/* 참가자 */}
          <button title="참가자">
            <i className="fa-solid fa-user"></i>
            <span>참가자</span>
          </button>

          {/* 방 나가기 */}
          <button className="room_exit" title="방 나가기">
            <i className="fa-solid fa-right-from-bracket"></i>
          </button>
        </footer>
      </div>
      {/* 참가자들 채팅 / 참가자 목록 */}
      <div className="pwf-chat_with_participants">
        {/* 채팅창 */}
        <div className="pwf-chat_container">
          <div className="type_menu">
            <span>채팅</span>
            {/* <i className="fa-solid fa-user"></i> */}
            <i className="fa-solid fa-comments"></i>
          </div>
          <ul className="chat_lists"></ul>
          <form action="" className="pwf-chat_form">
            <input />
            <button></button>
          </form>
        </div>
        {/* 참가자들 */}
        <div className="pwf-participants_container"></div>
      </div>
    </div>
  );
};
