import { act, useEffect, useRef, useState } from "react";
import "../css/login.css";
import { Link, useNavigate } from "react-router-dom";

import { FormSubmit, InputChange } from "../types/event";
import { emailFormValid, passwordFormValid } from "../validation/auth";
import { LoginError } from "../types/auth";
import { errorHandling } from "../error/error";
import { user_service } from "../service/userservice";
import { createFormData } from "../util/form";
import { emitter } from "../util/event";

interface InputProps {
  value: string;
  error: boolean;
  error_msg: string;
  show?: boolean;
  active: boolean;
}

export const Login = () => {
  const initState = {
    value: "",
    error: false,
    error_msg: "",
    show: false,
    active: false,
  };
  let [email, setEmail] = useState<InputProps>(initState);
  let [password, setPassword] = useState<InputProps>(initState);

  return (
    <div className="page login_page">
      {/* <div className="login_pwf_introduce"></div>
      <div className="login_info_box">
        <div></div>
      </div> */}
      <div className="login_input_box">
        <span
          style={{
            top: !email.active ? "50%" : "0",
            transition: "all 0.3s ease",
          }}
        >
          이메일
        </span>
        <input
          type="text"
          onFocus={() => setEmail((c) => ({ ...c, active: true }))}
          onBlur={() => setEmail((c) => ({ ...c, active: false }))}
        />
      </div>
    </div>
  );
};
