export const emailValidate = (email: string) => {
  let check_email = email.toString().trim().toLowerCase();
  return check_email.match(
    /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|.(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
  );
};

export const passwordValidate = (password: string) => {
  let check_password = password.trim().toLowerCase();
  console.log(check_password, check_password.length);
  return check_password.match(
    /^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!*?&])[A-Za-z\d@$!%*?&]{8,20}$/
  );
};
