import { ChangeEvent, useEffect, useState } from "react";
import "../css/stream.css";
import { User } from "../types/user";
import { FormSubmit } from "../types/event";

export const LiFriend = (data: any) => {
  let [select, Setselect] = useState<boolean>(false);

  const selectControl = () => {
    Setselect((c) => !c);
  };

  return (
    <li>
      <input
        id={data.nickname}
        type="radio"
        checked={select}
        value={data.nickname}
        onClick={selectControl}
      />
      {data.nickname}
    </li>
  );
};

export const StreamModal = () => {
  let [open, setOpen] = useState<boolean>(true);
  let [inviteUsers, setInviteUsers] = useState<User[]>([]);
  let [friendList, setFriendList] = useState<User[]>([
    {
      profile_img:
        "https://pwf-profile-img.s3.ap-northeast-2.amazonaws.com/user-image-icon-16.jpg",
      nickname: "test1",
    },
    {
      profile_img:
        "https://pwf-profile-img.s3.ap-northeast-2.amazonaws.com/user-image-icon-16.jpg",
      nickname: "test2",
    },
  ]);

  const submitStreamRoomInfo = async (e: FormSubmit) => {
    e.preventDefault();
  };

  return (
    <div className="modal_box" style={{ display: open ? "flex" : "none" }}>
      <div className="stream_modal">
        {/* 모달 헤더 */}
        <header>
          <button>X</button>
        </header>
        {/* 모달 내용 */}
        <div className="stream_create_box">
          <form onSubmit={submitStreamRoomInfo}>
            {/* 방 이름 */}
            <div className="stream_roomname_box">
              <label htmlFor="stream_roomname">방이름</label>
              <input type="text" className="pwf_roomname_input" />
            </div>

            {/* 참여자 목록 */}
            <div className="participants_box">
              <label htmlFor="participants">참여자 {inviteUsers.length}</label>
              <ul className="participants_list"></ul>
            </div>
            {/* 초대할 친구목록 */}
            <div className="friends_invite_box">
              <label htmlFor="friends_list">친구목록</label>
              <ul className="friends_list">
                {friendList.length
                  ? friendList.map((val) => <LiFriend data={val} />)
                  : "친구 없음"}
              </ul>
            </div>
          </form>
        </div>
        {/* 모달 풋터 */}
        <footer>
          <button>방 만들기</button>
        </footer>
      </div>
    </div>
  );
};
