import { io, Socket } from "socket.io-client";
import { User } from "../types/user";

export const socketClient: Socket = io("http://localhost:5000", {
  withCredentials: true,
  autoConnect: false,
  extraHeaders: {
    "my-custom-header": "abcd",
  },
});

interface getChatInfo {
  talk: string;
  userId: string;
}

socketClient.on("tests", (str: string) => {
  console.log(str);
});

socketClient.on("online friend", (user: User) => {
  console.log("online friend", user.nickname);
});

socketClient.on("offline friend", (user: User) => {
  console.log("offline friend", user.nickname);
  // console.log("offline friend", user);
});
