// export const createFormData:T = (data: T) => {
//   let formdata = new FormData();

// };

export function createFormData<T>(data: T): FormData {
  let formdata = new FormData();

  for (var key in data) {
    let val = data[key];
    console.log(key, typeof val, val);
    if (typeof val == "object")
      val.length
        ? val.forEach((val) => formdata.append(`${key}[]`, val))
        : formdata.append(`${key}`, "");
    else formdata.append(key, val);
  }
  return formdata;
}
