import "../css/modal.css";
import { useEffect, useState } from "react";

import { FriendModal } from "../components/modal/friend";
import { StreamModal } from "../components/modal/streamRoom";
import { useSelector } from "react-redux";
import { RootState } from "../context/store";

export const Modal = () => {
  let user = useSelector((state: RootState) => state.user);
  let [open, setOpen] = useState<boolean>(false);
  let [content, setContent] = useState<string>("");

  useEffect(() => {
    const handler = (data: any) => {
      let { select, nickname } = data;

      if (select) setInviteUsers((c) => [...c, nickname]);
      else setInviteUsers((c) => c.filter((user) => user !== nickname));
    };

    emitter.on("modal", handler);

    return () => {
      emitter.off("invite select", handler);
    };
  }, []);
  return (
    <div className="modal_box">
      <div className="modal_content">
        {/* 모달 헤더부분 */}
        <header className="modal_header">
          <button onClick={() => setOpen(false)}>X</button>
        </header>
        {/* 모달 콘텐츠 부분 */}
        {/* <FriendModal /> */}
        <StreamModal user={user} type={content} />
      </div>
    </div>
  );
};
