interface AuthResponse {
  state: boolean;
  type?: string;
}

export const userAuthValidation = (
  email: string,
  password: string
): AuthResponse => {
  let check_email = email
    .toString()
    .trim()
    .toLowerCase()
    .match(
      /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|.(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
    );

  if (!check_email) return { state: false, type: "email" };

  let check_password = password
    .trim()
    .toLowerCase()
    .match(/^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!*?&])[A-Za-z\d@$!%*?&]{8,20}$/);

  if (!check_password) return { state: false, type: "password" };
  return { state: true };
};
