import { useEffect } from "react";
import "../../../css/room/menu/participants.css";
import { Room } from "../../../types/room";
import { User } from "../../../types/user";

interface ParticipantsProps {
  user: User;
  state?: boolean;
  participants?: Room["participants"];
}

const ParticipantLi = ({ user }: any) => {
  return <li className="participant_li">{user.nickname} </li>;
};

export const Participants = ({
  user,
  state,
  participants,
}: ParticipantsProps) => {
  return (
    <div style={{ display: state ? "flex" : "none" }}>
      {participants?.length
        ? participants?.map((val) => <ParticipantLi user={val} />)
        : ""}
    </div>
  );
};
