// css
import "../css/signup.css";
import axios from "axios";

// library
import { Ref, use, useRef, useState } from "react";
import { Form, Link } from "react-router-dom";

// other file
import { createFormData } from "../util/form";
import { user_service } from "../service/userservice";
import { errorHandling } from "../error/error";

// types
import { User } from "../types/user";
import { authErrorProps, SignupError } from "../types/auth";
import { InputChange, FormSubmit } from "../types/event";
import { emailValidate, passwordValidate } from "../validation/auth";

interface InputProps {
  error: boolean;
  error_msg: string;
  value: string;
}

export const Signup = () => {
  const initialState: InputProps = {
    error: false,
    error_msg: "",
    value: "",
  };
  let [SignupStep, setSignupStep] = useState<boolean>(false);

  let emailRef = useRef<HTMLInputElement | null>(null);
  let nicknameRef = useRef<HTMLInputElement | null>(null);
  let passwordRef = useRef<HTMLInputElement | null>(null);
  let passwordCheckRef = useRef<HTMLInputElement | null>(null);

  let [email, setEmail] = useState<InputProps>(initialState);
  let [nickname, setNickname] = useState<InputProps>(initialState);
  let [password, setPassword] = useState<InputProps>(initialState);
  let [password_check, setPasswordCheck] = useState<InputProps>(initialState);

  // 인증부분
  let [authcode, setAuthcode] = useState<string>("");
  let AuthInputRef = useRef<HTMLInputElement | null>(null);

  // 유저 정보 보내기
  const submitUserInfo = async (e: FormSubmit) => {
    e.preventDefault();
  };

  // const errorExistcheck = () => {
  //   if (email.value == "" || email.error) return false;
  //   if (nickname.value == "" || nickname.error) return false;
  //   if (password.value == "" || password.error) return false;
  //   if (password_check.value == "" || password_check.error) return false;
  //   return true;
  // };

  const ErrorHandle = (type: string, msg: string) => {
    if (type == "email")
      setEmail((c) => ({ ...c, error: true, error_msg: msg }));
    else setNickname((c) => ({ ...c, error: true, error_msg: msg }));
  };

  // 이메일 검증
  // const emailValid = async () => {
  //   try {
  //     if (!emailValidate(email.value))
  //       throw { type: "email", msg: SignupError.EMAIL };

  //     let formdata = createFormData({ type: "email", value: email.value });
  //     let res = await user_service.OverlapCheck(formdata);
  //     setEmail((c) => ({ ...c, error: false }));
  //   } catch (e) {
  //     let { type, msg } = errorHandling(e);
  //     ErrorHandle(type, msg);
  //   }
  // };
  // 닉네임 검증
  const nicknameValid = async () => {
    try {
      let formdata = createFormData({
        type: "nickname",
        value: nickname.value,
      });
      let res = await user_service.OverlapCheck(formdata);
      setNickname((c) => ({ ...c, error: false }));
    } catch (err) {
      console.log(err);
      let { type, msg } = errorHandling(err);
      ErrorHandle(type, msg);
    }
  };
  // 비밀번호 검증
  const passwordValid = () => {
    let result = passwordValidate(password.value);
    setPassword((c) => ({
      ...c,
      error: !result,
      error_msg: !result ? SignupError.PASSWORD : "",
    }));
  };
  // 비밀번화 확인값 검증
  const passwordCheckValid = (e: InputChange) => {
    let value = e.target.value;
    let valid = password.value === value;
    setPasswordCheck((c) => ({
      ...c,
      value,
      error: !valid,
      error_msg: valid ? "" : SignupError.PASSWORD_CHECK,
    }));
  };

  const testing = async () => {
    try {
      let formdata = createFormData({ email });
      let code = await user_service.testing(formdata);
      console.log(code);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="page signup_page">
      {SignupStep ? (
        // 회원가입 이메일 인증
        <div className="signup_auth_box">
          {/* header section */}
          <div className="signup_auth_header">
            {email.value}로 인증번호가 전송되었습니다
            <button className="signup_auth_resend_btn">번호 재전송</button>
          </div>
          {/* content section */}
          <div className="signup_auth_content">
            <form method="post">
              <input
                type="text"
                ref={AuthInputRef}
                value={authcode}
                placeholder="인증번호를 입력하세요"
                onChange={(e: InputChange) => setAuthcode(e.target.value)}
              />

              <button className="">인증하기</button>
            </form>
          </div>
          <footer className="signup_auth_footer">
            <button onClick={() => setSignupStep(false)}>돌아가기</button>
          </footer>
        </div>
      ) : (
        // 회원가입 유저정보
        <div className="signup_container">
          {/* 로그인 헤더 */}
          <div className="signup_header">
            <span>PWF</span>
            <button onClick={testing}>test</button>
          </div>

          {/* 로그인 내용 */}
          <div className="signup_content">
            <form action="post" onSubmit={submitUserInfo}>
              {/* 이메일 */}
              <div className="signup_input_box">
                <input
                  type="email"
                  style={{
                    border: `1px solid var(--pwf-${
                      email.error ? "red" : "gray"
                    })`,
                  }}
                  spellCheck={false}
                  placeholder="이메일"
                  ref={emailRef}
                  value={email.value}
                  onBlur={() => emailValidate(email.value, setEmail)}
                  onChange={(e: InputChange) =>
                    setEmail((c) => ({ ...c, value: e.target.value }))
                  }
                />
                <span
                  className="signup_error"
                  style={{ display: email.error ? "block" : "none" }}
                >
                  {email.error_msg}
                </span>
              </div>
              {/* 닉네임 */}
              <div className="signup_input_box">
                <input
                  type="text"
                  placeholder="닉네임"
                  style={{
                    border: `1px solid var(--pwf-${
                      nickname.error ? "red" : "gray"
                    })`,
                  }}
                  ref={nicknameRef}
                  spellCheck={false}
                  value={nickname.value}
                  onBlur={nicknameValid}
                  onChange={(e: InputChange) =>
                    setNickname((c) => ({ ...c, value: e.target.value }))
                  }
                />
                <span
                  className="signup_error"
                  style={{ display: nickname.error ? "block" : "none" }}
                >
                  {nickname.error_msg}
                </span>
              </div>
              {/* 비밀번호 */}
              <div className="signup_input_box">
                <input
                  type="password"
                  placeholder="비밀번호"
                  style={{
                    border: `1px solid var(--pwf-${
                      password.error ? "red" : "gray"
                    })`,
                  }}
                  ref={passwordRef}
                  value={password.value}
                  onBlur={passwordValid}
                  onChange={(e: InputChange) =>
                    setPassword((c) => ({ ...c, value: e.target.value }))
                  }
                />
                <span
                  className="signup_error"
                  style={{ display: password.error ? "block" : "none" }}
                >
                  {password.error_msg}
                </span>
              </div>
              {/* 비밀번호 확인 */}
              <div className="signup_input_box">
                <input
                  type="password"
                  style={{
                    border: `1px solid var(--pwf-${
                      password_check.error ? "red" : "gray"
                    })`,
                    backgroundColor: password.value === "" ? "" : "",
                  }}
                  placeholder="비밀번호 확인"
                  ref={passwordCheckRef}
                  value={password_check.value}
                  disabled={password.value === "" ? true : false}
                  onChange={passwordCheckValid}
                />
                <span
                  className="signup_error"
                  style={{ display: password_check.error ? "block" : "none" }}
                >
                  {password_check.error_msg}
                </span>
              </div>

              <button>회원가입</button>
            </form>
          </div>

          {/* 로그인 풋터 */}
          <div className="signup_footer">
            <span>계정이 있다면?</span>
            <Link to="/login" className="login_anchor">
              로그인
            </Link>
          </div>
        </div>
      )}
    </div>
  );
};
