import { useRef, useState } from "react";
import "../css/login.css";
import { Link } from "react-router-dom";

import { InputChange } from "../types/event";

export const Login = () => {
  let emailRef = useRef<HTMLInputElement | null>(null);
  let passwordRef = useRef<HTMLInputElement | null>(null);

  let [Email, setEmail] = useState<string>("");
  let [Password, setPassword] = useState<string>("");
  let [loginError, setLoginError] = useState<{ error: boolean; msg: string }>({
    error: false,
    msg: "",
  });

  // async

  return (
    <div className="page login_page">
      <div className="login_container">
        {/* 로그인 헤더 */}
        <header className="login_header">
          <span>PWF</span>
        </header>
        {/* 로그인 내용 */}
        <div className="login_content">
          <form action="post">
            <input
              type="email"
              spellCheck={false}
              placeholder="이메일"
              ref={emailRef}
              value={Email}
              onChange={(e: InputChange) => setEmail(e.target.value)}
            />
            <input
              type="password"
              value={Password}
              ref={passwordRef}
              placeholder="비밀번호"
              onChange={(e: InputChange) => setPassword(e.target.value)}
            />
            <button>로그인</button>
          </form>
        </div>
        {/* 로그인 풋터 */}
        <footer className="login_footer">
          <Link to="/signup">회원가입</Link>

          <Link to="/">비밀번호 찾기</Link>
        </footer>
      </div>
    </div>
  );
};
