// css
import "../css/signup.css";

import { useState } from "react";
import { Link } from "react-router-dom";

// type
import { InputChange, FormSubmit } from "../types/event";
import { User } from "../../../types/user";

export const Signup = () => {
  let [Email, setEmail] = useState<string>("");
  let [Nickname, setNickname] = useState<string>("");
  let [Password, setPassword] = useState<string>("");
  let [PasswordCheck, setPasswordCheck] = useState<string>("");

  const submitUserInfo = async (e: FormSubmit) => {
    e.preventDefault();

    let data: User = {
      email: Email,
      nickname: Nickname,
      password: Password,
      password_check: PasswordCheck,
    };
  };

  return (
    <div className="page signup_page">
      <div className="signup_container">
        {/* 로그인 헤더 */}
        <header className="signup_header">
          <span>PWF</span>
        </header>
        {/* 로그인 내용 */}
        <div className="signup_content">
          <form action="post" onSubmit={submitUserInfo}>
            <input
              type="email"
              spellCheck={false}
              placeholder="이메일"
              value={Email}
              onChange={(e: InputChange) => setEmail(e.target.value)}
            />
            <input
              type="text"
              placeholder="닉네임"
              spellCheck={false}
              value={Nickname}
              onChange={(e: InputChange) => setNickname(e.target.value)}
            />
            <input
              type="password"
              placeholder="비밀번호"
              value={Password}
              onChange={(e: InputChange) => setPassword(e.target.value)}
            />
            <input
              type="password"
              placeholder="비밀번호 확인"
              value={PasswordCheck}
              onChange={(e: InputChange) => setPasswordCheck(e.target.value)}
            />
            <button>회원가입</button>
          </form>
        </div>
        {/* 로그인 풋터 */}
        <footer className="signup_footer">
          <span>계정이 있다면?</span>
          <Link to="/login" className="login_anchor">
            로그인
          </Link>
        </footer>
      </div>
    </div>
  );
};
