// css
import "../css/signup.css";

// library
import { Link } from "react-router-dom";
import { useRef, useState } from "react";

// other file
import { emailValidate, passwordValidate } from "../util/validate";
import { user_service } from "../service/userservice";

// types
import { FormSubmit, InputChange } from "../types/event";

export const SignupUser = () => {
  let [Error, setError] = useState<authErrorProps>({
    state: false,
    message: "",
  });
  let emailRef = useRef<HTMLInputElement | null>(null);
  let nicknameRef = useRef<HTMLInputElement | null>(null);
  let passwordRef = useRef<HTMLInputElement | null>(null);
  let passwordCheckRef = useRef<HTMLInputElement | null>(null);

  let [Email, setEmail] = useState<string>("");
  let [Nickname, setNickname] = useState<string>("");
  let [Password, setPassword] = useState<string>("");
  let [PasswordCheck, setPasswordCheck] = useState<string>("");

  const submitUserInfo = async (e: FormSubmit) => {
    e.preventDefault();

    const errorHandler = (message: string) =>
      setError((c: object) => ({ ...c, state: true, message }));

    if (!emailValidate(Email)) {
      errorHandler("이메일 형식이 올바르지 않습니다");
      if (emailRef.current) emailRef.current.focus();
      return;
    } else if (!passwordValidate(Password)) {
      errorHandler(
        "비밀번호는 영문자 , 숫자 , 특수문자를 포함하여 8~20자 이내여야 합니다"
      );
      if (passwordRef.current) passwordRef.current.focus();
      return;
    } else if (Password !== PasswordCheck) {
      errorHandler("비밀번호와 비밀번화 확인값이 다릅니다");
      if (passwordCheckRef.current) passwordCheckRef.current.focus();
      return;
    }

    let formdata = new FormData();
    formdata.append("email", Email);
    formdata.append("nickname", Nickname.trim());
    formdata.append("password", Password);
    formdata.append("password_check", PasswordCheck);

    try {
      let res = await user_service.createUser(formdata);
    } catch (err) {
      console.log(err);
      alert(err);
    }
  };

  return (
    <div className="signup_container">
      {/* 로그인 헤더 */}
      <header className="signup_header">
        <span>PWF</span>
      </header>
      {/* 로그인 내용 */}
      <div className="signup_content">
        <form action="post" onSubmit={submitUserInfo}>
          {/* error box */}
          <div
            className="error_box"
            style={{ display: Error.state ? "flex" : "none" }}
          >
            {Error.message}
          </div>
          <input
            type="email"
            spellCheck={false}
            placeholder="이메일"
            ref={emailRef}
            value={Email}
            onChange={(e: InputChange) => setEmail(e.target.value)}
          />
          <input
            type="text"
            placeholder="닉네임"
            ref={nicknameRef}
            spellCheck={false}
            value={Nickname}
            onChange={(e: InputChange) => setNickname(e.target.value)}
          />
          <input
            type="password"
            placeholder="비밀번호"
            ref={passwordRef}
            value={Password}
            onChange={(e: InputChange) => setPassword(e.target.value)}
          />
          <input
            type="password"
            placeholder="비밀번호 확인"
            ref={passwordCheckRef}
            value={PasswordCheck}
            onChange={(e: InputChange) => setPasswordCheck(e.target.value)}
          />
          <button>회원가입</button>
        </form>
      </div>
      {/* 로그인 풋터 */}
      <footer className="signup_footer">
        <span>계정이 있다면?</span>
        <Link to="/login" className="login_anchor">
          로그인
        </Link>
      </footer>
    </div>
  );
};
